body.messageBody:
{
    "type": "AdaptiveCard",
    "body": [
        {
            "type": "TextBlock",
            "size": "large",
            "weight": "bolder",
            "text": "Azure Sentinel playbook has taken an action on a risky user",
            "wrap": true
        },
        {
            "type": "ColumnSet",
            "columns": [
                {
                    "type": "Column",
                    "items": [
                        {
                            "type": "Image",
                            "style": "Person",
                            "url": "https://connectoricons-prod.azureedge.net/releases/v1.0.1391/1.0.1391.2130/azuresentinel/icon.png",
                            "size": "Small"
                        }
                    ],
                    "width": "auto"
                }              
            ]
        },
                 {
                            "type": "TextBlock",
                            "text": "Possible compromised user detected by the @{triggerBody()?['object']?['properties']?['severity']} Incident @{triggerBody()?['object']?['properties']?['title']}",
                            "wrap": true,
                            "weight":"bolder"
                        },
                        {
                            "type": "TextBlock",
                            "text": "Incident number: @{triggerBody()?['object']?['properties']?['incidentNumber']}",
                            "wrap": true,
                            "weight":"bolder"
                        },
         {
                            "type": "TextBlock",
                            "text": "Incident description",
                            "wrap": true,
                            "weight":"bolder"
                        },
                         {
                            "type": "TextBlock",
                            "text": " @{triggerBody()?['object']?['properties']?['description']}",
                            "wrap": true                       
                        },

                       {
            "type": "TextBlock",
            "text": "[Click here to view Incident]()",
            "wrap":"true"
        },                   

        {
            "type": "ColumnSet",
            "columns": [

                {
                    "type": "Column",
                    "items": [

                         {
                            "type": "TextBlock",
                            "text": "The Okta user in risk:",
                            "wrap": true,
                            "weight":"bolder"
                        },
                         {
                            "type": "TextBlock",
                            "text": "User ID: @{body('Get_User')?['id']}",
                            "wrap": true
                        },
                        {
                            "type": "TextBlock",
                            "text": "First and Last name: @{body('Get_User')?['profile']?['firstName']} @{body('Get_User')?['profile']?['lastName']}",
                            "wrap": true
                        },
                         {
                            "type": "TextBlock",
                            "text": "User display name: @{items('For_each-risky_account_received_from_the_incident')?['Name']}",
                            "wrap": true
                        },
                         {
                            "type": "TextBlock",
                            "text": "Action taken:",

                            "weight": "bolder",
                            "wrap": true
                        },
                        {
                            "type": "TextBlock",
                            "text": "User password was reset",

                            "wrap": true
                        },
                         {
                            "type": "TextBlock",
                            "text": "User sessions were cleared",

                            "wrap": true
                        },
                        {
                            "type": "TextBlock",
                            "text": "Please investigate further in Okta.",
                            "weight":"bolder",
                            "wrap": true
                        }
                    ],
                    "width": "auto"

                }
            ]
        }

    ], 
      "actions": [
                    {
                        "type": "Action.Submit",
                        "title": "OK"
                    }
                ],

    "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
    "version": "1.2"
}

{
    "type": "AdaptiveCard",
    "body": [
        {
            "type": "TextBlock",
            "size": "large",
            "weight": "bolder",
            "text": "New Incident from Azure Sentinel. Please respond ASAP",
            "wrap": true
        },
        {
            "type": "ColumnSet",
            "columns": [
                {
                    "type": "Column",
                    "items": [
                        {
                            "type": "Image",
                            "style": "Person",
                            "url": "https://connectoricons-prod.azureedge.net/releases/v1.0.1391/1.0.1391.2130/azuresentinel/icon.png",
                            "size": "Small"
                        }
                    ],
                    "width": "auto"
                }
            ]
        },
        {
            "type": "TextBlock",
            "size": "Medium",
            "text": "Incident Description ",
            "wrap": true,
            "weight": "Bolder"
        },
        {
            "type": "TextBlock",
            "size": "Medium",
            "text":" @{triggerBody()?['object']?['properties']?['description']}",
            "wrap": true
        },
        {
            "type": "TextBlock",
            "size": "Medium",
            "text": "Please confirm it was you",
            "wrap": true,
            "weight": "Bolder"
        }
    ],
    "actions": [
        {
            "type": "Action.Submit",
            "title": "This was me"
        },
        {
            "type": "Action.Submit",
            "title": "This was not me"
        }
    ],
    "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
    "version": "1.2"
}

 {
    "type": "AdaptiveCard",
    "body": [

        {
            "type": "TextBlock",
            "size": "large",
            "weight": "bolder",
            "text": "Suspicious identity - Azure Sentinel",
            "wrap": true
        },
         {
                            "type": "TextBlock",
                            "text": "Possible Comprised User detected by the provider",
                            "wrap": true
                        },
         {
            "type": "TextBlock",
            "text": "@{triggerBody()?['object']?['properties']?['severity']} incident @{triggerBody()?['object']?['properties']?['title']} ",
            "wrap": true,
            "weight":"bolder"
        },

         {
                            "type": "TextBlock",
                            "text": "Incident description",
                            "wrap": true,
                            "weight": "Bolder"
                        },
                        {
                            "type": "TextBlock",
                            "text":" @{triggerBody()?['object']?['properties']?['description']}",
                            "wrap": true
                        },
                         {
                            "type": "TextBlock",
                            "text": "[Click here to view the Incident](@{triggerBody()?['object']?['properties']?['incidentUrl']})",
                            "wrap": true
                        },
        {
            "type": "ColumnSet",
            "columns": [
                {
                    "type": "Column",
                    "items": [

                        {
                            "type": "TextBlock",
                            "text": "Risky user details from Okta:",
                            "wrap": true,
                            "weight": "Bolder"
                        },
                        {
                            "type": "TextBlock",
                            "text": "UserID: @{body('Get_User')?['id']}",
                            "wrap": true
                        },
                        {
                            "type": "TextBlock",
                            "text": "First and Last name: @{body('Get_User')?['profile']?['firstName']} @{body('Get_User')?['profile']?['lastName']}",
                            "wrap": true
                        },
                       {
                            "type": "TextBlock",
                            "text": "User display name: @{items('For_each-risky_account_received_from_the_incident')?['Name']}",
                            "wrap": true
                        },
                     {
                            "type": "TextBlock",
                            "text": "Status: @{body('Get_User')?['status']}",
                            "wrap": true
                        },
                        {
                            "type": "TextBlock",
                            "text": "Status changed: @{body('Get_User')?['statusChanged']}",
                            "wrap": true
                        },
                       {
                            "type": "TextBlock",
                            "text": "Last login: @{body('Get_User')?['lastLogin']}",
                            "wrap": true
                        },
                        {
                            "type": "TextBlock",
                            "text": "Last updated: @{body('Get_User')?['lastUpdated']}",
                            "wrap": true
                        },
                         {
                            "type": "TextBlock",
                            "text": "Password changed: @{body('Get_User')?['passwordChanged']}",
                            "wrap": true
                        },
                        {
                            "type": "TextBlock",
                            "size": "Medium",
                            "weight": "Bolder",
                            "text": "Incident configuration:",
                            "wrap": true
                        }
                    ],
                    "width": "auto"
                }
            ]
        },
        {
            "type": "ColumnSet",
            "columns": [
                {
                    "type": "Column",
                    "items": [
                        {
                            "type": "Image",
                            "style": "Person",
                            "url": "https://connectoricons-prod.azureedge.net/releases/v1.0.1391/1.0.1391.2130/azuresentinel/icon.png",
                            "size": "Small"
                        }
                    ],
                    "width": "auto"
                }
            ]
        },
        {
            "type": "TextBlock",
            "text": "Close Azure Sentinal incident?"
        },
        {
            "choices": [
                {
                    "isSelected": true,
                    "title": "Close incident - False Positive",
                    "value": "False Positive - Incorrect Alert Logic"
                },
                {
                    "title": "Close incident - True Positive",
                    "value": "True Positive - Suspicious Activity"
                },
                {
                    "title": "Close incident - Benign Positive",
                    "value": "Benign Positive - Suspicious But Expected"
                },
                {
                    "title": "No",
                    "value": "no"
                }
            ],
            "id": "incidentStatus",
            "style": "compact",
            "type": "Input.ChoiceSet",
            "value": "Benign Positive - Suspicious But Expected"
        },
        {
            "type": "TextBlock",
            "text": "Change Azure Sentinel incident severity?"
        },
        {
            "choices": [
                {
                    "title": "Medium",
                    "value": "Medium"
                },
                {
                    "title": "High",
                    "value": "High"
                },
                {
                    "title": "Low",
                    "value": "Low"
                },
                {
                    "title": "Don't change",
                    "value": "same"
                }
            ],
            "id": "incidentSeverity",
            "style": "compact",
            "type": "Input.ChoiceSet",
            "value": "@{triggerBody()?['object']?['properties']?['severity']}"
        },
        {
            "type": "TextBlock",
            "text": "Response in Okta",
            "size": "Medium",
            "weight": "Bolder"
        },
        {
            "type": "Image",
            "style": "Person",
            "url": "https://seekvectorlogo.com/wp-content/uploads/2017/12/okta-vector-logo.png",
            "size": "Small"
        }
    ],
    "actions": [
        {
            "type": "Action.Submit",
            "title": "Change user state (suspend)"
        },
        {
            "type": "Action.Submit",
            "title": "Change user state (unsuspend)"
        },
        {
            "type": "Action.Submit",
            "title": "Expire password"
        },
        {
            "type": "Action.Submit",
            "title": "Reset password"
        },
        {
            "type": "Action.Submit",
            "title": "Ignore"
        },
        {
            "type": "Action.ShowCard",
            "title": "Add user to group",
            "card": {
                "type": "AdaptiveCard",
                 "body":[
                     {
            "type": "Input.ChoiceSet",
            "id": "GroupId",

            "value": "1",
            "choices": @{outputs('Select_groups')?[0]?['body']}

        }
                ],
                "actions": [
                    {
                        "type": "Action.Submit",
                        "title": "OK"
                    }
                ],
                "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
                "version": "1.2"
            }
        }
    ],
    "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
    "version": "1.2"
}
